/*
 * SensorService_AddService- Initializes the SensorService service by registering
 *          GATT attributes with the GATT server.
 *
 *    rspTaskId - The ICall Task Id that should receive responses for Indications.
 */
extern bStatus_t SensorService_AddService( uint8_t rspTaskId )
{
  uint8_t status;

  // Allocate Client Characteristic Configuration table
  ss_DATAConfig = (gattCharCfg_t *)ICall_malloc( sizeof(gattCharCfg_t) * linkDBNumConns );
  if ( ss_DATAConfig == NULL )
  {
    return ( bleMemAllocError );
  }

  // Initialize Client Characteristic Configuration attributes
  GATTServApp_InitCharCfg( INVALID_CONNHANDLE, ss_DATAConfig );
  // Register GATT attribute list and CBs with GATT Server App
  status = GATTServApp_RegisterService( SENSOR_SERVICEAttrTbl,
                                        GATT_NUM_ATTRS( SENSOR_SERVICEAttrTbl ),
                                        GATT_MAX_ENCRYPT_KEY_SIZE,
                                        &SENSOR_SERVICECBs );
  Log_info1("Registered service, %d attributes", (IArg)GATT_NUM_ATTRS( SENSOR_SERVICEAttrTbl ));
  ss_icall_rsp_task_id = rspTaskId;

  return ( status );
}

/*
 * SensorService_RegisterAppCBs - Registers the application callback function.
 *                    Only call this function once.
 *
 *    appCallbacks - pointer to application callbacks.
 */
bStatus_t SensorService_RegisterAppCBs( SensorServiceCBs_t *appCallbacks )
{
  if ( appCallbacks )
  {
    pAppCBs = appCallbacks;
    Log_info1("Registered callbacks to application. Struct %p", (IArg)appCallbacks);
    return ( SUCCESS );
  }
  else
  {
    Log_warning0("Null pointer given for app callbacks.");
    return ( FAILURE );
  }
}

/*
 * SensorService_SetParameter - Set a SensorService parameter.
 *
 *    param - Profile parameter ID
 *    len   - length of data to write
 *    value - pointer to data to write.  This is dependent on
 *            the parameter ID and may be cast to the appropriate
 *            data type (example: data type of uint16_t will be cast to
 *            uint16_t pointer).
 */
bStatus_t SensorService_SetParameter( uint8_t param, uint16_t len, void *value )
{
  bStatus_t ret = SUCCESS;
  uint8_t  *pAttrVal;
  uint16_t *pValLen;
  uint16_t valMinLen;
  uint16_t valMaxLen;
  uint8_t sendNotiInd = FALSE;
  gattCharCfg_t *attrConfig;
  uint8_t needAuth;

  switch ( param )
  {
    case SS_ACC_ENABLE_ID:
      pAttrVal  =  ss_ACC_ENABLEVal;
      pValLen   = &ss_ACC_ENABLEValLen;
      valMinLen =  SS_ACC_ENABLE_LEN_MIN;
      valMaxLen =  SS_ACC_ENABLE_LEN;
      Log_info2("SetParameter : %s len: %d", (IArg)"ACC_ENABLE", (IArg)len);
      break;

    case SS_ACC_ODR_ID:
      pAttrVal  =  ss_ACC_ODRVal;
      pValLen   = &ss_ACC_ODRValLen;
      valMinLen =  SS_ACC_ODR_LEN_MIN;
      valMaxLen =  SS_ACC_ODR_LEN;
      Log_info2("SetParameter : %s len: %d", (IArg)"ACC_ODR", (IArg)len);
      break;

    case SS_ACC_FS_ID:
      pAttrVal  =  ss_ACC_FSVal;
      pValLen   = &ss_ACC_FSValLen;
      valMinLen =  SS_ACC_FS_LEN_MIN;
      valMaxLen =  SS_ACC_FS_LEN;
      Log_info2("SetParameter : %s len: %d", (IArg)"ACC_FS", (IArg)len);
      break;

    case SS_GYRO_FS_ID:
      pAttrVal  =  ss_GYRO_FSVal;
      pValLen   = &ss_GYRO_FSValLen;
      valMinLen =  SS_GYRO_FS_LEN_MIN;
      valMaxLen =  SS_GYRO_FS_LEN;
      Log_info2("SetParameter : %s len: %d", (IArg)"GYRO_FS", (IArg)len);
      break;

    case SS_GYRO_ODR_ID:
      pAttrVal  =  ss_GYRO_ODRVal;
      pValLen   = &ss_GYRO_ODRValLen;
      valMinLen =  SS_GYRO_ODR_LEN_MIN;
      valMaxLen =  SS_GYRO_ODR_LEN;
      Log_info2("SetParameter : %s len: %d", (IArg)"GYRO_ODR", (IArg)len);
      break;

    case SS_GYRO_ENABLE_ID:
      pAttrVal  =  ss_GYRO_ENABLEVal;
      pValLen   = &ss_GYRO_ENABLEValLen;
      valMinLen =  SS_GYRO_ENABLE_LEN_MIN;
      valMaxLen =  SS_GYRO_ENABLE_LEN;
      Log_info2("SetParameter : %s len: %d", (IArg)"GYRO_ENABLE", (IArg)len);
      break;

    case SS_DATA_ID:
      pAttrVal  =  ss_DATAVal;
      pValLen   = &ss_DATAValLen;
      valMinLen =  SS_DATA_LEN_MIN;
      valMaxLen =  SS_DATA_LEN;
      sendNotiInd = TRUE;
      attrConfig  = ss_DATAConfig;
      needAuth    = FALSE; // Change if authenticated link is required for sending.
      Log_info2("SetParameter : %s len: %d", (IArg)"DATA", (IArg)len);
      break;

    default:
      Log_error1("SetParameter: Parameter #%d not valid.", (IArg)param);
      return INVALIDPARAMETER;
  }

  // Check bounds, update value and send notification or indication if possible.
  if ( len <= valMaxLen && len >= valMinLen )
  {
    memcpy(pAttrVal, value, len);
    *pValLen = len; // Update length for read and get.

    if (sendNotiInd)
    {
      Log_info2("Transmitting noti/ind- connHandle %d, %s", (IArg)attrConfig[0].connHandle,
                                                    (IArg)( (attrConfig[0].value==0)?"Noti/ind disabled":
                                                    (attrConfig[0].value==1)?"Notification enabled":
                                                     "Indication enabled" ) );
      // Try to send notification.
      GATTServApp_ProcessCharCfg( attrConfig, pAttrVal, needAuth,
                                  SENSOR_SERVICEAttrTbl, GATT_NUM_ATTRS( SENSOR_SERVICEAttrTbl ),
                                  ss_icall_rsp_task_id,  SENSOR_SERVICE_ReadAttrCB);
    }
  }
  else
  {
    Log_error3("Length outside bounds: Len: %d MinLen: %d MaxLen: %d.", (IArg)len, (IArg)valMinLen, (IArg)valMaxLen);
    ret = bleInvalidRange;
  }

  return ret;
}


/*
 * SensorService_GetParameter - Get a SensorService parameter.
 *
 *    param - Profile parameter ID
 *    len   - pointer to a variable that contains the maximum length that can be written to *value.
              After the call, this value will contain the actual returned length.
 *    value - pointer to data to write.  This is dependent on
 *            the parameter ID and may be cast to the appropriate
 *            data type (example: data type of uint16_t will be cast to
 *            uint16_t pointer).
 */
bStatus_t SensorService_GetParameter( uint8_t param, uint16_t *len, void *value )
{
  bStatus_t ret = SUCCESS;
  switch ( param )
  {
    case SS_ACC_ENABLE_ID:
      *len = MIN(*len, ss_ACC_ENABLEValLen);
      memcpy(value, ss_ACC_ENABLEVal, *len);
      Log_info2("GetParameter : %s returning %d bytes", (IArg)"ACC_ENABLE", (IArg)*len);
      break;

    case SS_ACC_ODR_ID:
      *len = MIN(*len, ss_ACC_ODRValLen);
      memcpy(value, ss_ACC_ODRVal, *len);
      Log_info2("GetParameter : %s returning %d bytes", (IArg)"ACC_ODR", (IArg)*len);
      break;

    case SS_ACC_FS_ID:
      *len = MIN(*len, ss_ACC_FSValLen);
      memcpy(value, ss_ACC_FSVal, *len);
      Log_info2("GetParameter : %s returning %d bytes", (IArg)"ACC_FS", (IArg)*len);
      break;

    case SS_GYRO_FS_ID:
      *len = MIN(*len, ss_GYRO_FSValLen);
      memcpy(value, ss_GYRO_FSVal, *len);
      Log_info2("GetParameter : %s returning %d bytes", (IArg)"GYRO_FS", (IArg)*len);
      break;

    case SS_GYRO_ODR_ID:
      *len = MIN(*len, ss_GYRO_ODRValLen);
      memcpy(value, ss_GYRO_ODRVal, *len);
      Log_info2("GetParameter : %s returning %d bytes", (IArg)"GYRO_ODR", (IArg)*len);
      break;

    case SS_GYRO_ENABLE_ID:
      *len = MIN(*len, ss_GYRO_ENABLEValLen);
      memcpy(value, ss_GYRO_ENABLEVal, *len);
      Log_info2("GetParameter : %s returning %d bytes", (IArg)"GYRO_ENABLE", (IArg)*len);
      break;

    default:
      Log_error1("GetParameter: Parameter #%d not valid.", (IArg)param);
      ret = INVALIDPARAMETER;
      break;
  }
  return ret;
}

/*********************************************************************
 * @internal
 * @fn          SENSOR_SERVICE_findCharParamId
 *
 * @brief       Find the logical param id of an attribute in the service's attr table.
 *
 *              Works only for Characteristic Value attributes and
 *              Client Characteristic Configuration Descriptor attributes.
 *
 * @param       pAttr - pointer to attribute
 *
 * @return      uint8_t paramID (ref SENSOR_SERVICE.h) or 0xFF if not found.
 */
static uint8_t SENSOR_SERVICE_findCharParamId(gattAttribute_t *pAttr)
{
  // Is this a Client Characteristic Configuration Descriptor?
  if (ATT_BT_UUID_SIZE == pAttr->type.len && GATT_CLIENT_CHAR_CFG_UUID == *(uint16_t *)pAttr->type.uuid)
    return SENSOR_SERVICE_findCharParamId(pAttr - 1); // Assume the value attribute precedes CCCD and recurse

  // Is this attribute in "ACC ENABLE"?
  else if ( ATT_BT_UUID_SIZE == pAttr->type.len && !memcmp(pAttr->type.uuid, ss_ACC_ENABLEUUID, pAttr->type.len))
    return SS_ACC_ENABLE_ID;

  // Is this attribute in "ACC ODR"?
  else if ( ATT_BT_UUID_SIZE == pAttr->type.len && !memcmp(pAttr->type.uuid, ss_ACC_ODRUUID, pAttr->type.len))
    return SS_ACC_ODR_ID;

  // Is this attribute in "ACC FS"?
  else if ( ATT_BT_UUID_SIZE == pAttr->type.len && !memcmp(pAttr->type.uuid, ss_ACC_FSUUID, pAttr->type.len))
    return SS_ACC_FS_ID;

  // Is this attribute in "GYRO FS"?
  else if ( ATT_BT_UUID_SIZE == pAttr->type.len && !memcmp(pAttr->type.uuid, ss_GYRO_FSUUID, pAttr->type.len))
    return SS_GYRO_FS_ID;

  // Is this attribute in "GYRO ODR"?
  else if ( ATT_BT_UUID_SIZE == pAttr->type.len && !memcmp(pAttr->type.uuid, ss_GYRO_ODRUUID, pAttr->type.len))
    return SS_GYRO_ODR_ID;

  // Is this attribute in "GYRO ENABLE"?
  else if ( ATT_BT_UUID_SIZE == pAttr->type.len && !memcmp(pAttr->type.uuid, ss_GYRO_ENABLEUUID, pAttr->type.len))
    return SS_GYRO_ENABLE_ID;

  // Is this attribute in "DATA"?
  else if ( ATT_BT_UUID_SIZE == pAttr->type.len && !memcmp(pAttr->type.uuid, ss_DATAUUID, pAttr->type.len))
    return SS_DATA_ID;

  else
    return 0xFF; // Not found. Return invalid.
}

/*********************************************************************
 * @fn          SENSOR_SERVICE_ReadAttrCB
 *
 * @brief       Read an attribute.
 *
 * @param       connHandle - connection message was received on
 * @param       pAttr - pointer to attribute
 * @param       pValue - pointer to data to be read
 * @param       pLen - length of data to be read
 * @param       offset - offset of the first octet to be read
 * @param       maxLen - maximum length of data to be read
 * @param       method - type of read message
 *
 * @return      SUCCESS, blePending or Failure
 */
static bStatus_t SENSOR_SERVICE_ReadAttrCB( uint16_t connHandle, gattAttribute_t *pAttr,
                                       uint8_t *pValue, uint16_t *pLen, uint16_t offset,
                                       uint16_t maxLen, uint8_t method )
{
  bStatus_t status = SUCCESS;
  uint16_t valueLen;
  uint8_t paramID = 0xFF;

  // Find settings for the characteristic to be read.
  paramID = SENSOR_SERVICE_findCharParamId( pAttr );
  switch ( paramID )
  {
    case SS_ACC_ENABLE_ID:
      valueLen = ss_ACC_ENABLEValLen;

      Log_info4("ReadAttrCB : %s connHandle: %d offset: %d method: 0x%02x",
                 (IArg)"ACC_ENABLE",
                 (IArg)connHandle,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for ACC ENABLE can be inserted here */
      break;

    case SS_ACC_ODR_ID:
      valueLen = ss_ACC_ODRValLen;

      Log_info4("ReadAttrCB : %s connHandle: %d offset: %d method: 0x%02x",
                 (IArg)"ACC_ODR",
                 (IArg)connHandle,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for ACC ODR can be inserted here */
      break;

    case SS_ACC_FS_ID:
      valueLen = ss_ACC_FSValLen;

      Log_info4("ReadAttrCB : %s connHandle: %d offset: %d method: 0x%02x",
                 (IArg)"ACC_FS",
                 (IArg)connHandle,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for ACC FS can be inserted here */
      break;

    case SS_GYRO_FS_ID:
      valueLen = ss_GYRO_FSValLen;

      Log_info4("ReadAttrCB : %s connHandle: %d offset: %d method: 0x%02x",
                 (IArg)"GYRO_FS",
                 (IArg)connHandle,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for GYRO FS can be inserted here */
      break;

    case SS_GYRO_ODR_ID:
      valueLen = ss_GYRO_ODRValLen;

      Log_info4("ReadAttrCB : %s connHandle: %d offset: %d method: 0x%02x",
                 (IArg)"GYRO_ODR",
                 (IArg)connHandle,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for GYRO ODR can be inserted here */
      break;

    case SS_GYRO_ENABLE_ID:
      valueLen = ss_GYRO_ENABLEValLen;

      Log_info4("ReadAttrCB : %s connHandle: %d offset: %d method: 0x%02x",
                 (IArg)"GYRO_ENABLE",
                 (IArg)connHandle,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for GYRO ENABLE can be inserted here */
      break;

    case SS_DATA_ID:
      valueLen = ss_DATAValLen;

      Log_info4("ReadAttrCB : %s connHandle: %d offset: %d method: 0x%02x",
                 (IArg)"DATA",
                 (IArg)connHandle,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for DATA can be inserted here */
      break;

    default:
      Log_error0("Attribute was not found.");
      return ATT_ERR_ATTR_NOT_FOUND;
  }
  // Check bounds and return the value
  if ( offset > valueLen )  // Prevent malicious ATT ReadBlob offsets.
  {
    Log_error0("An invalid offset was requested.");
    status = ATT_ERR_INVALID_OFFSET;
  }
  else
  {
    *pLen = MIN(maxLen, valueLen - offset);  // Transmit as much as possible
    memcpy(pValue, pAttr->pValue + offset, *pLen);
  }

  return status;
}

/*********************************************************************
 * @fn      SENSOR_SERVICE_WriteAttrCB
 *
 * @brief   Validate attribute data prior to a write operation
 *
 * @param   connHandle - connection message was received on
 * @param   pAttr - pointer to attribute
 * @param   pValue - pointer to data to be written
 * @param   len - length of data
 * @param   offset - offset of the first octet to be written
 * @param   method - type of write message
 *
 * @return  SUCCESS, blePending or Failure
 */
static bStatus_t SENSOR_SERVICE_WriteAttrCB( uint16_t connHandle, gattAttribute_t *pAttr,
                                        uint8_t *pValue, uint16_t len, uint16_t offset,
                                        uint8_t method )
{
  bStatus_t status  = SUCCESS;
  uint8_t   paramID = 0xFF;
  uint8_t   changeParamID = 0xFF;
  uint16_t writeLenMin;
  uint16_t writeLenMax;
  uint16_t *pValueLenVar;

  // See if request is regarding a Client Characterisic Configuration
  if (ATT_BT_UUID_SIZE == pAttr->type.len && GATT_CLIENT_CHAR_CFG_UUID == *(uint16_t *)pAttr->type.uuid)
  {
    // Allow notification and indication, but do not check if really allowed per CCCD.
    status = GATTServApp_ProcessCCCWriteReq( connHandle, pAttr, pValue, len,
                                             offset, GATT_CLIENT_CFG_NOTIFY |
                                                     GATT_CLIENT_CFG_INDICATE );
    if (SUCCESS == status && pAppCBs && pAppCBs->pfnCfgChangeCb)
       pAppCBs->pfnCfgChangeCb( connHandle, SENSOR_SERVICE_SERV_UUID,
                                SENSOR_SERVICE_findCharParamId(pAttr), pValue, len );

     return status;
  }

  // Find settings for the characteristic to be written.
  paramID = SENSOR_SERVICE_findCharParamId( pAttr );
  switch ( paramID )
  {
    case SS_ACC_ENABLE_ID:
      writeLenMin  = SS_ACC_ENABLE_LEN_MIN;
      writeLenMax  = SS_ACC_ENABLE_LEN;
      pValueLenVar = &ss_ACC_ENABLEValLen;

      Log_info5("WriteAttrCB : %s connHandle(%d) len(%d) offset(%d) method(0x%02x)",
                 (IArg)"ACC_ENABLE",
                 (IArg)connHandle,
                 (IArg)len,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for ACC ENABLE can be inserted here */
      break;

    case SS_ACC_ODR_ID:
      writeLenMin  = SS_ACC_ODR_LEN_MIN;
      writeLenMax  = SS_ACC_ODR_LEN;
      pValueLenVar = &ss_ACC_ODRValLen;

      Log_info5("WriteAttrCB : %s connHandle(%d) len(%d) offset(%d) method(0x%02x)",
                 (IArg)"ACC_ODR",
                 (IArg)connHandle,
                 (IArg)len,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for ACC ODR can be inserted here */
      break;

    case SS_ACC_FS_ID:
      writeLenMin  = SS_ACC_FS_LEN_MIN;
      writeLenMax  = SS_ACC_FS_LEN;
      pValueLenVar = &ss_ACC_FSValLen;

      Log_info5("WriteAttrCB : %s connHandle(%d) len(%d) offset(%d) method(0x%02x)",
                 (IArg)"ACC_FS",
                 (IArg)connHandle,
                 (IArg)len,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for ACC FS can be inserted here */
      break;

    case SS_GYRO_FS_ID:
      writeLenMin  = SS_GYRO_FS_LEN_MIN;
      writeLenMax  = SS_GYRO_FS_LEN;
      pValueLenVar = &ss_GYRO_FSValLen;

      Log_info5("WriteAttrCB : %s connHandle(%d) len(%d) offset(%d) method(0x%02x)",
                 (IArg)"GYRO_FS",
                 (IArg)connHandle,
                 (IArg)len,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for GYRO FS can be inserted here */
      break;

    case SS_GYRO_ODR_ID:
      writeLenMin  = SS_GYRO_ODR_LEN_MIN;
      writeLenMax  = SS_GYRO_ODR_LEN;
      pValueLenVar = &ss_GYRO_ODRValLen;

      Log_info5("WriteAttrCB : %s connHandle(%d) len(%d) offset(%d) method(0x%02x)",
                 (IArg)"GYRO_ODR",
                 (IArg)connHandle,
                 (IArg)len,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for GYRO ODR can be inserted here */
      break;

    case SS_GYRO_ENABLE_ID:
      writeLenMin  = SS_GYRO_ENABLE_LEN_MIN;
      writeLenMax  = SS_GYRO_ENABLE_LEN;
      pValueLenVar = &ss_GYRO_ENABLEValLen;

      Log_info5("WriteAttrCB : %s connHandle(%d) len(%d) offset(%d) method(0x%02x)",
                 (IArg)"GYRO_ENABLE",
                 (IArg)connHandle,
                 (IArg)len,
                 (IArg)offset,
                 (IArg)method);
      /* Other considerations for GYRO ENABLE can be inserted here */
      break;

    default:
      Log_error0("Attribute was not found.");
      return ATT_ERR_ATTR_NOT_FOUND;
  }
  // Check whether the length is within bounds.
  if ( offset >= writeLenMax )
  {
    Log_error0("An invalid offset was requested.");
    status = ATT_ERR_INVALID_OFFSET;
  }
  else if ( offset + len > writeLenMax )
  {
    Log_error0("Invalid value length was received.");
    status = ATT_ERR_INVALID_VALUE_SIZE;
  }
  else if ( offset + len < writeLenMin && ( method == ATT_EXECUTE_WRITE_REQ || method == ATT_WRITE_REQ ) )
  {
    // Refuse writes that are lower than minimum.
    // Note: Cannot determine if a Reliable Write (to several chars) is finished, so those will
    //       only be refused if this attribute is the last in the queue (method is execute).
    //       Otherwise, reliable writes are accepted and parsed piecemeal.
    Log_error0("Invalid value length was received.");
    status = ATT_ERR_INVALID_VALUE_SIZE;
  }
  else
  {
    // Copy pValue into the variable we point to from the attribute table.
    memcpy(pAttr->pValue + offset, pValue, len);

    // Only notify application and update length if enough data is written.
    //
    // Note: If reliable writes are used (meaning several attributes are written to using ATT PrepareWrite),
    //       the application will get a callback for every write with an offset + len larger than _LEN_MIN.
    // Note: For Long Writes (ATT Prepare + Execute towards only one attribute) only one callback will be issued,
    //       because the write fragments are concatenated before being sent here.
    if ( offset + len >= writeLenMin )
    {
      changeParamID = paramID;
      *pValueLenVar = offset + len; // Update data length.
    }
  }

  // Let the application know something changed (if it did) by using the
  // callback it registered earlier (if it did).
  if (changeParamID != 0xFF)
    if ( pAppCBs && pAppCBs->pfnChangeCb )
      pAppCBs->pfnChangeCb( connHandle, SENSOR_SERVICE_SERV_UUID, paramID, pValue, len+offset ); // Call app function from stack task context.

  return status;
}
